mapper.insert:
/* insertUser */
INSERT INTO TCMN_USER (
       ACCOUNT_ID
     , PWD
     , ROLE
     , EMAIL
) VALUES (
       #{accountId}
     , #{pwd}
     , #{role}
     , #{email}
)

mapper.select:
/* selectUser */
SELECT USER_ID AS userId
     , EMAIL AS email
     , ROLE AS role
  FROM TCMN_USER
 WHERE ACCOUNT_ID = #{accountId}
   AND PWD = #{pwd}
